import React, { useState, useEffect } from "react";

export default function Input({ onSend, value }) {
  const [question, setQuestion] = useState('');

  useEffect(() => {
    setQuestion(value); // Update the input with the transcribed text
  }, [value]);

  const handleInputChange = (e) => {
    setQuestion(e.target.value);
  };

  const handleSend = (e) => {
    e.preventDefault();
    if (question.trim()) {
      onSend(question); // Send the question to the parent component
      setQuestion(""); // Clear the input field after sending
    }
  };

  return (
    <div className="input">
      <form onSubmit={handleSend}>
        <input
          type="text"
          onChange={handleInputChange}
          value={question}
          placeholder="Enter your message here"
        />
        <button type="submit">
          <svg
            version="1.1"
            xmlns="http://www.w3.org/2000/svg"
            width="20"
            height="20"
            viewBox="0 0 500 500"
          >
            <g>
              <g>
                <polygon points="0,497.25 535.5,267.75 0,38.25 0,216.75 382.5,267.75 0,318.75" />
              </g>
            </g>
          </svg>
        </button>
      </form>
    </div>
  );
}
